			$ideas[] = get_entity((int) $iguid);	
		}
	}

	elgg_push_context('widgets');
	$river_body = '';
	if (is_array($ideas)) {
		elgg_echo ('<ul class="shareit-ranking" id="shareit-ranking-list">');
