def categorize_movie_titles(titles):
    categories = {
        "short": [],
        "medium": [],
        "long": []
    }

    for title in titles:
        num_words = len(title.split())
        if num_words < 5:
            categories["short"].append(title)
        elif 5 <= num_words <= 8:
            categories["medium"].append(title)
        else:
            categories["long"].append(title)

    return categories

def translate_phrases(phrases, translation_dict):
    translated_phrases = []
    for phrase in phrases:
        translated_words = []
        for word in phrase.split():
            translated_word = translation_dict.get(word, word)
            translated_words.append(translated_word)
        translated_phrase = ' '.join(translated_words)
        translated_phrases.append(translated_phrase)
    return translated_phrases

def calculate_annual_support(monthly_support):
    total_support = 0
    for amount in monthly_support:
        total_support += amount
    return total_support

